<?php	require_once 'config.php';	header('Content-type: application/json');	if (isset($_REQUEST['tags'])) {		$term = trim(strtolower($_REQUEST['tags'])); // trim the query		$term = str_replace(array(" ", "(", ")"), array("", "", ""), $term);		$url = "https://api.instagram.com/v1/tags/". $term ."/media/recent?access_token=". ACCESS_TOKEN;		try {			$data = file_get_contents($url);			if (empty($data)) {				throw new Exception("[\"empty data\"]");			}			print ($data);		} catch (Exception $e) {				die($e->getMessage());		}	} else if (isset($_REQUEST['users'])) {		$term = trim(strtolower($_REQUEST['users'])); // trim the query		$term = str_replace(array(" ", "(", ")"), array("", "", ""), $term);		$url = "https://api.instagram.com/v1/users/search?q=". $term ."&count=10&access_token=". ACCESS_TOKEN;		try {			$data = file_get_contents($url);			if (empty($data)) {				throw new Exception("[\"empty data\"]");			}			print ($data);		} catch (Exception $e) {				die($e->getMessage());		}	} else {		echo ("[\"nothing found\"]");	}?>